	public Species getFromName(String name)
	{
			try 
			{
				java.lang.reflect.Field field = getClass().getDeclaredField(name.toLowerCase());
				//below wants the instance of the class
				//but since it is static, it is irrelevant what the instance is,
				//so passing null will not create errors
				Species species = (Species)field.get(new SpeciesList());
				return species;
			} 
			catch (NoSuchFieldException | SecurityException | IllegalArgumentException | IllegalAccessException e) 
			{
				e.printStackTrace();
				return placeholder;
			}
	}
